/** * Enqueue scripts and styles. */
function your_theme_scripts()
{ wp_enqueue_style( 'style', get_stylesheet_uri() ); }
add_action( 'wp_enqueue_scripts','your_theme_scripts' );



// this theme has one menu location
register_nav_menu('primary', 'primary menu horizontal' );



//I decided to remore the more tag generated by the_excerpt and manualy add link to each post
//I did this because exceprt more is not run on posts that have a manual more tag
function wpdocs_excerpt_more( $more){
  return '';
}
add_filter('excerpt_more','wpdocs_excerpt_more');

//add Thumbnail support
add_theme_support('post-thumbnails');
set_post_thumbnail_size(300,450);

//Thumnail size
add_image_size('custom-thumb-200',200,350,true);
add_image_size('custom-thumb-300',300,450,true);
add_image_size('custom-thumb-600',600,900,true);
add_image_size('custom-thumb-800',800,1100,false);

//add widget area functionality
add_action('widgets_init','mytheme_widgets_init');
function mytheme_widgets_init()
{ $args = array(
  'name' => __('sidebar1'),
  'id'   => 'sidebar1',
  'description' => 'this is my sidebar',
  'class' => 'sidebarclass',
  'before_widget' => '<div>', //note we have used section rather than the default li
  'after_widget' => '</div>',
  'before_title' => '<h3>', //use h3 tag to match html design
  'after_title' => '</h3>'  );
  register_sidebar( $args );
}

